import { httpAction } from '@/api/manage';
import pick from 'lodash.pick';
import { duplicateCheck } from '@/api/api';
import JEditor from '@/components/jeecg/JEditor';
export default {
  name: "SysMessageTemplateModal",
  components: {
    JEditor: JEditor
  },
  data: function data() {
    return {
      title: "操作",
      visible: false,
      disable: true,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {
        templateCode: {
          rules: [{
            required: true,
            message: '请输入模板CODE!'
          }, {
            validator: this.validateTemplateCode
          }]
        },
        templateName: {
          rules: [{
            required: true,
            message: '请输入模板标题!'
          }]
        },
        templateContent: {
          rules: []
        },
        templateType: {
          rules: [{
            required: true,
            message: '请输入模板类型!'
          }]
        }
      },
      url: {
        add: "/message/sysMessageTemplate/add",
        edit: "/message/sysMessageTemplate/edit"
      },
      useEditor: false,
      templateEditorContent: ""
    };
  },
  created: function created() {},
  methods: {
    add: function add() {
      this.disable = false;
      this.edit({});
    },
    edit: function edit(record) {
      var _this = this;

      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.useEditor = record.templateType == 2;

      if (this.useEditor) {
        this.templateEditorContent = record.templateContent;
      } else {
        this.templateEditorContent = '';
      }

      this.visible = true;
      this.$nextTick(function () {
        if (_this.useEditor) {
          _this.form.setFieldsValue(pick(_this.model, 'templateCode', 'templateName', 'templateTestJson', 'templateType'));
        } else {
          _this.form.setFieldsValue(pick(_this.model, 'templateCode', 'templateContent', 'templateName', 'templateTestJson', 'templateType'));
        }
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
      this.disable = true;
    },
    handleOk: function handleOk() {
      var _this2 = this;

      this.model.templateType = this.templateType;
      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this2.model.id) {
            httpurl += _this2.url.add;
            method = 'post';
          } else {
            httpurl += _this2.url.edit;
            method = 'put';
          }

          var formData = Object.assign(_this2.model, values); //时间格式化

          if (_this2.useEditor) {
            formData.templateContent = _this2.templateEditorContent;
          }

          console.log(formData);
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    validateTemplateCode: function validateTemplateCode(rule, value, callback) {
      var params = {
        tableName: "sys_sms_template",
        fieldName: "template_code",
        fieldVal: value,
        dataId: this.model.id
      };
      duplicateCheck(params).then(function (res) {
        if (res.success) {
          callback();
        } else {
          callback(res.message);
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    handleChangeTemplateType: function handleChangeTemplateType(value) {
      //如果是邮件类型那么则改变模板内容是富文本编辑器
      this.useEditor = value == 2;
    }
  }
};
