import Bar from '@/components/chart/Bar';
import Pie from '@/components/chart/Pie';
import ACol from 'ant-design-vue/es/grid/Col';
import { getAction } from '@/api/manage';
export default {
  name: 'ArchivesStatisticst',
  components: {
    ACol: ACol,
    Bar: Bar,
    Pie: Pie
  },
  data: function data() {
    return {
      description: '档案统计页面',
      // 查询条件
      queryParam: {},
      // 数据集
      countSource: [],
      // 柱状图
      barType: 'year',
      barDate: ['month', 'month'],
      barValue: [],
      // 饼状图
      pieType: 'year',
      pieDate: ['month', 'month'],
      pieValue: [],
      // 统计图类型
      tabStatus: "bar",
      url: {
        getYearCountInfo: "/api/report/getYearCountInfo",
        getMonthCountInfo: "/api/report/getMonthCountInfo",
        getCntrNoCountInfo: "/api/report/getCntrNoCountInfo",
        getCabinetCountInfo: "/api/report/getCabinetCountInfo"
      }
    };
  },
  created: function created() {
    var url = this.url.getYearCountInfo;
    this.loadDate(url, 'year', {});
  },
  methods: {
    loadDate: function loadDate(url, type, param) {
      var _this = this;

      getAction(url, param, 'get').then(function (res) {
        if (res.success) {
          _this.countSource = [];

          if (type === 'year') {
            _this.getYearCountSource(res.result);
          }

          if (type === 'month') {
            _this.getMonthCountSource(res.result);
          }

          if (type === 'category') {
            _this.getCategoryCountSource(res.result);
          }

          if (type === 'cabinet') {
            _this.getCabinetCountSource(res.result);
          }
        } else {
          var that = _this;
          that.$message.warning(res.message);
        }
      });
    },
    getYearCountSource: function getYearCountSource(data) {
      for (var i = 0; i < data.length; i++) {
        if (this.tabStatus === "bar") {
          this.countSource.push({
            x: "".concat(data[i].year, "\u5E74"),
            y: data[i].yearcount
          });
        } else {
          this.countSource.push({
            item: "".concat(data[i].year, "\u5E74"),
            count: data[i].yearcount
          });
        }
      }
    },
    getMonthCountSource: function getMonthCountSource(data) {
      for (var i = 0; i < data.length; i++) {
        if (this.tabStatus === "bar") {
          this.countSource.push({
            x: data[i].month,
            y: data[i].monthcount
          });
        } else {
          this.countSource.push({
            item: data[i].month,
            count: data[i].monthcount
          });
        }
      }
    },
    getCategoryCountSource: function getCategoryCountSource(data) {
      for (var i = 0; i < data.length; i++) {
        if (this.tabStatus === "bar") {
          this.countSource.push({
            x: data[i].classifyname,
            y: data[i].cntrnocount
          });
        } else {
          this.countSource.push({
            item: data[i].classifyname,
            count: data[i].cntrnocount
          });
        }
      }
    },
    getCabinetCountSource: function getCabinetCountSource(data) {
      for (var i = 0; i < data.length; i++) {
        if (this.tabStatus === "bar") {
          this.countSource.push({
            x: data[i].cabinetname,
            y: data[i].cabinetcocunt
          });
        } else {
          this.countSource.push({
            item: data[i].cabinetname,
            count: data[i].cabinetcocunt
          });
        }
      }
    },
    // 选择统计图类别
    callback: function callback(key) {
      if (key === "1") {
        this.tabStatus = "bar";
        this.queryDatebar();
      } else {
        this.tabStatus = "pie";
        this.queryDatepie();
      }
    },
    // 选择统计类别
    statisticst: function statisticst(e) {
      if (this.tabStatus === "pie") {
        this.pieType = e.target.value;
        this.queryDatepie();
      } else {
        this.barType = e.target.value;
        this.queryDatebar();
      }
    },
    // 按月份查询
    queryDatebar: function queryDatebar() {
      if (this.barValue.length > 0) {
        this.getUrl(this.barType, {
          startTime: this.barValue[0]._d,
          endTime: this.barValue[1]._d
        });
      } else {
        this.getUrl(this.barType, {});
      }
    },
    queryDatepie: function queryDatepie() {
      if (this.pieValue.length > 0) {
        this.getUrl(this.pieType, {
          startTime: this.pieValue[0]._d,
          endTime: this.pieValue[1]._d
        });
      } else {
        this.getUrl(this.pieType, {});
      }
    },
    searchReset: function searchReset() {
      console.log(this.tabStatus);

      if (this.tabStatus === "pie") {
        this.pieValue = [];
      } else {
        this.barValue = [];
      }

      this.getUrl(this.barType, {});
    },
    // 选择请求url
    getUrl: function getUrl(type, param) {
      var url = "";

      if (type === 'year') {
        url = this.url.getYearCountInfo;
      }

      if (type === 'month') {
        url = this.url.getMonthCountInfo;
      }

      if (type === 'category') {
        url = this.url.getCntrNoCountInfo;
      }

      if (type === 'cabinet') {
        url = this.url.getCabinetCountInfo;
      }

      this.loadDate(url, type, param);
    },
    // 选择月份日期
    handleBarDate: function handleBarDate(value, mode) {
      this.barValue = value;
      this.barDate = [mode[0] === 'date' ? 'month' : mode[0], mode[1] === 'date' ? 'month' : mode[1]];
    },
    handlePieDate: function handlePieDate(value, mode) {
      this.pieValue = value;
      this.pieDate = [mode[0] === 'date' ? 'month' : mode[0], mode[1] === 'date' ? 'month' : mode[1]];
    }
  }
};
