import { httpAction } from '@/api/manage';
import JCron from "@/components/jeecg/JCron.vue";
import pick from 'lodash.pick';
import moment from "moment";
export default {
  name: "QuartzJobModal",
  components: {
    JCron: JCron
  },
  data: function data() {
    return {
      title: "操作",
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      cron: {
        label: '',
        value: ''
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {
        cron: {
          rules: [{
            required: true,
            message: '请输入cron表达式!'
          }]
        }
      },
      url: {
        add: "/sys/quartzJob/add",
        edit: "/sys/quartzJob/edit"
      }
    };
  },
  created: function created() {},
  methods: {
    add: function add() {
      this.edit({});
    },
    edit: function edit(record) {
      var _this = this;

      this.model = Object.assign({}, record);
      console.log(this.model);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'jobClassName', 'cronExpression', 'parameter', 'description', 'status'));
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this2 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        console.log('values', values);

        if (!err) {
          // if (typeof values.cronExpression == "undefined" || Object.keys(values.cronExpression).length==0 ) {
          //   this.$message.warning('请输入cron表达式!');
          //   return false;
          // }
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this2.model.id) {
            httpurl += _this2.url.add;
            method = 'post';
          } else {
            httpurl += _this2.url.edit;
            method = 'put';
          }

          var formData = Object.assign(_this2.model, values); //时间格式化

          console.log('提交参数', formData);
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    setCorn: function setCorn(data) {
      var _this3 = this;

      console.log('data)', data);
      this.$nextTick(function () {
        _this3.model.cronExpression = data;
      }); // console.log(Object.keys(data).length==0);
      // if (Object.keys(data).length==0) {
      //   this.$message.warning('请输入cron表达式!');
      // }
    },
    validateCron: function validateCron(rule, value, callback) {
      if (!value) {
        callback();
      } else if (Object.keys(value).length == 0) {
        callback("请输入cron表达式!");
      }
    }
  }
};
