import PermissionModal from "./modules/PermissionModal";
import { getPermissionList } from '@/api/api';
import { JeecgListMixin } from '@/mixins/JeecgListMixin';
import PermissionDataRuleList from "./PermissionDataRuleList";
import JEllipsis from '@/components/jeecg/JEllipsis';
var columns = [{
  title: '菜单名称',
  dataIndex: 'name',
  key: 'name'
}, {
  title: '菜单类型',
  dataIndex: 'menuType',
  key: 'menuType',
  customRender: function customRender(text) {
    if (text == 0) {
      return '菜单';
    } else if (text == 1) {
      return '菜单';
    } else if (text == 2) {
      return '按钮/权限';
    } else {
      return text;
    }
  }
},
/*{
title: '权限编码',
dataIndex: 'perms',
key: 'permissionCode',
},*/
{
  title: 'icon',
  dataIndex: 'icon',
  key: 'icon'
}, {
  title: '组件',
  dataIndex: 'component',
  key: 'component',
  scopedSlots: {
    customRender: 'component'
  }
}, {
  title: '路径',
  dataIndex: 'url',
  key: 'url',
  scopedSlots: {
    customRender: 'url'
  }
}, {
  title: '排序',
  dataIndex: 'sortNo',
  key: 'sortNo'
}, {
  title: '操作',
  dataIndex: 'action',
  scopedSlots: {
    customRender: 'action'
  },
  align: 'center',
  width: 150
}];
export default {
  name: 'PermissionList',
  mixins: [JeecgListMixin],
  components: {
    PermissionDataRuleList: PermissionDataRuleList,
    PermissionModal: PermissionModal,
    JEllipsis: JEllipsis
  },
  data: function data() {
    return {
      description: '这是菜单管理页面',
      // 表头
      columns: columns,
      loading: false,
      url: {
        list: '/sys/permission/list',
        delete: '/sys/permission/delete',
        deleteBatch: '/sys/permission/deleteBatch'
      }
    };
  },
  methods: {
    loadData: function loadData() {
      var _this = this;

      this.dataSource = [];
      getPermissionList().then(function (res) {
        if (res.success) {
          console.log(res.result);
          _this.dataSource = res.result;
        }
      });
    },
    // 打开数据规则编辑
    handleDataRule: function handleDataRule(record) {
      this.$refs.PermissionDataRuleList.edit(record);
    },
    handleAddSub: function handleAddSub(record) {
      this.$refs.modalForm.title = "添加子菜单";
      this.$refs.modalForm.localMenuType = 1;
      this.$refs.modalForm.disableSubmit = false;
      this.$refs.modalForm.edit({
        status: '1',
        permsType: '1',
        route: true,
        'parentId': record.id
      });
    }
  }
};
