import JeecgOrderCustomerModal from "./form/JeecgOrderCustomerModal";
import JeecgOrderDMainList from "./JeecgOrderDMainList";
import { JeecgListMixin } from '@/mixins/JeecgListMixin';
import { getAction } from '@/api/manage';
export default {
  name: "JeecgOrderCustomerList",
  mixins: [JeecgListMixin],
  components: {
    JeecgOrderDMainList: JeecgOrderDMainList,
    JeecgOrderCustomerModal: JeecgOrderCustomerModal
  },
  data: function data() {
    return {
      description: '订单客户信息',
      // 表头
      columns: [{
        title: '客户名',
        align: "center",
        width: 100,
        dataIndex: 'name',
        key: 'name'
      }, {
        title: '性别',
        align: "center",
        dataIndex: 'sex',
        customRender: function customRender(text) {
          if (text == 1) {
            return "男";
          } else if (text == 2) {
            return "女";
          } else {
            return text;
          }
        }
      }, {
        title: '身份证号码',
        align: "center",
        dataIndex: 'idcard'
      }, {
        title: '电话',
        dataIndex: 'telphone',
        align: "center"
      }, {
        title: '操作',
        key: 'operation',
        align: 'center',
        width: 130,
        scopedSlots: {
          customRender: 'action'
        }
      }],
      url: {
        list: "/test/order/listOrderCustomerByMainId",
        delete: "/test/order/deleteCustomer",
        deleteBatch: "/test/order/deleteBatchCustomer"
      }
    };
  },
  methods: {
    loadData: function loadData(arg) {
      var _this = this;

      if (arg === 1) {
        this.ipagination.current = 1;
      }

      var params = this.getQueryParams();
      getAction(this.url.list, {
        mainId: params.mainId
      }).then(function (res) {
        if (res.success) {
          _this.dataSource = res.result;
        } else {
          _this.dataSource = null;
        }
      });
    },
    getOrderMain: function getOrderMain(orderId) {
      this.queryParam.mainId = orderId;
      this.loadData(1);
    },
    handleAdd: function handleAdd() {
      this.$refs.modalForm.add(this.queryParam.mainId);
      this.$refs.modalForm.title = "添加客户信息";
    }
  }
};
