import { httpAction, getAction } from '@/api/manage';
import JDate from '@/components/jeecg/JDate';
import pick from 'lodash.pick';
import moment from "moment";
export default {
  name: "JeecgOrderMainModal",
  components: {
    JDate: JDate
  },
  data: function data() {
    return {
      title: "操作",
      visible: false,
      orderMainModel: {
        jeecgOrderCustomerList: [{}],
        jeecgOrderTicketList: [{}]
      },
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {},
      url: {
        add: "/test/jeecgOrderMain/add",
        edit: "/test/jeecgOrderMain/edit",
        orderCustomerList: "/test/jeecgOrderMain/queryOrderCustomerListByMainId",
        orderTicketList: "/test/jeecgOrderMain/queryOrderTicketListByMainId"
      }
    };
  },
  created: function created() {},
  methods: {
    add: function add() {
      this.edit({});
    },
    edit: function edit(record) {
      var _this = this;

      this.form.resetFields();
      this.orderMainModel = Object.assign({}, record);
      this.orderMainModel.jeecgOrderCustomerList = [{}];
      this.orderMainModel.jeecgOrderTicketList = [{}]; //--------------------------------------------------------
      //初始化明细表数据

      console.log(this.orderMainModel.id);

      if (this.orderMainModel.id) {
        var params = {
          id: this.orderMainModel.id //初始化订单机票列表

        };
        getAction(this.url.orderCustomerList, params).then(function (res) {
          if (res.success) {
            _this.orderMainModel.jeecgOrderCustomerList = res.result;

            _this.$forceUpdate();
          }
        }); //初始化订单客户列表

        getAction(this.url.orderTicketList, params).then(function (res) {
          if (res.success) {
            _this.orderMainModel.jeecgOrderTicketList = res.result;

            _this.$forceUpdate();
          }
        });
      } //--------------------------------------------------------


      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.orderMainModel, 'orderCode', 'ctype', 'orderMoney', 'content'));

        _this.form.setFieldsValue({
          orderDate: _this.orderMainModel.orderDate ? moment(_this.orderMainModel.orderDate) : null
        }); //时间格式化

      });
      console.log(this.orderMainModel);
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this2 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this2.orderMainModel.id) {
            httpurl += _this2.url.add;
            method = 'post';
          } else {
            httpurl += _this2.url.edit;
            method = 'put';
          }

          var orderMainData = Object.assign(_this2.orderMainModel, values); //时间格式化

          orderMainData.orderDate = orderMainData.orderDate ? orderMainData.orderDate.format('YYYY-MM-DD HH:mm:ss') : null;

          var formData = _objectSpread({}, orderMainData, {
            jeecgOrderCustomerList: orderMainData.jeecgOrderCustomerList,
            jeecgOrderTicketList: orderMainData.jeecgOrderTicketList
          });

          console.log(formData);
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    addRowCustom: function addRowCustom() {
      this.orderMainModel.jeecgOrderCustomerList.push({});
      this.$forceUpdate();
    },
    delRowCustom: function delRowCustom(index) {
      console.log(index);
      this.orderMainModel.jeecgOrderCustomerList.splice(index, 1);
      this.$forceUpdate();
    },
    addRowTicket: function addRowTicket() {
      this.orderMainModel.jeecgOrderTicketList.push({});
      console.log(this.orderMainModel.jeecgOrderTicketList);
      this.$forceUpdate();
    },
    delRowTicket: function delRowTicket(index) {
      console.log(index);
      this.orderMainModel.jeecgOrderTicketList.splice(index, 1);
      this.$forceUpdate();
    }
  }
};
