import { getAction, putAction } from '@/api/manage';
import ShowAnnouncement from "./ShowAnnouncement";
import store from '@/store/';
export default {
  name: "HeaderNotice",
  components: {
    ShowAnnouncement: ShowAnnouncement
  },
  data: function data() {
    return {
      loadding: false,
      url: {
        listCementByUser: "/sys/annountCement/listByUser",
        editCementSend: "/sys/sysAnnouncementSend/editByAnntIdAndUserId",
        queryById: "/sys/annountCement/queryById"
      },
      hovered: false,
      announcement1: [],
      announcement2: [],
      msg1Count: "3",
      msg2Count: "0",
      msg1Title: "通知(3)",
      msg2Title: "",
      stopTimer: false
    };
  },
  computed: {
    msgTotal: function msgTotal() {
      return parseInt(this.msg1Count) + parseInt(this.msg2Count);
    }
  },
  mounted: function mounted() {
    this.loadData(); //this.timerFun();

    this.initWebSocket();
  },
  destroyed: function destroyed() {
    // 离开页面生命周期函数
    this.websocketclose();
  },
  methods: {
    timerFun: function timerFun() {
      var _this = this;

      this.stopTimer = false;
      var myTimer = setInterval(function () {
        // 停止定时器
        if (_this.stopTimer == true) {
          clearInterval(myTimer);
          return;
        }

        _this.loadData();
      }, 6000);
    },
    loadData: function loadData() {
      var _this2 = this;

      try {
        // 获取系统消息
        getAction(this.url.listCementByUser).then(function (res) {
          if (res.success) {
            _this2.announcement1 = res.result.anntMsgList;
            _this2.msg1Count = res.result.anntMsgTotal;
            _this2.msg1Title = "通知(" + res.result.anntMsgTotal + ")";
            _this2.announcement2 = res.result.sysMsgList;
            _this2.msg2Count = res.result.sysMsgTotal;
            _this2.msg2Title = "系统消息(" + res.result.sysMsgTotal + ")";
          }
        }).catch(function (error) {
          console.log("系统消息通知异常", error); //这行打印permissionName is undefined

          _this2.stopTimer = true;
          console.log("清理timer");
        });
      } catch (err) {
        this.stopTimer = true;
        console.log("通知异常", err);
      }
    },
    fetchNotice: function fetchNotice() {
      var _this3 = this;

      if (this.loadding) {
        this.loadding = false;
        return;
      }

      this.loadding = true;
      setTimeout(function () {
        _this3.loadding = false;
      }, 200);
    },
    showAnnouncement: function showAnnouncement(record) {
      var _this4 = this;

      putAction(this.url.editCementSend, {
        anntId: record.id
      }).then(function (res) {
        if (res.success) {
          _this4.loadData();
        }
      });
      this.hovered = false;
      this.$refs.ShowAnnouncement.detail(record);
    },
    toMyAnnouncement: function toMyAnnouncement() {
      this.$router.push({
        path: '/isps/userAnnouncement',
        name: 'isps-userAnnouncement'
      });
    },
    modalFormOk: function modalFormOk() {},
    handleHoverChange: function handleHoverChange(visible) {
      this.hovered = visible;
    },
    initWebSocket: function initWebSocket() {
      // WebSocket与普通的请求所用协议有所不同，ws等同于http，wss等同于https
      var userId = store.getters.userInfo.id;
      var url = window._CONFIG['domianURL'].replace("https://", "wss://").replace("http://", "ws://") + "/websocket/" + userId; //console.log(url);

      this.websock = new WebSocket(url);
      this.websock.onopen = this.websocketonopen;
      this.websock.onerror = this.websocketonerror;
      this.websock.onmessage = this.websocketonmessage;
      this.websock.onclose = this.websocketclose;
    },
    websocketonopen: function websocketonopen() {
      console.log("WebSocket连接成功");
    },
    websocketonerror: function websocketonerror(e) {
      console.log("WebSocket连接发生错误");
    },
    websocketonmessage: function websocketonmessage(e) {
      console.log("-----接收消息-------", e.data);
      var data = eval("(" + e.data + ")"); //解析对象

      this.loadData(); //if(data.cmd == "topic"){
      //系统通知

      this.openNotification(data); //}else if(data.cmd == "user"){
      //用户消息
      //  this.openNotification(data);
      //}
    },
    websocketclose: function websocketclose(e) {
      console.log("connection closed (" + e.code + ")");
    },
    openNotification: function openNotification(data) {
      var _this5 = this;

      var text = data.msgTxt;
      var key = "open".concat(Date.now());
      this.$notification.open({
        message: '消息提醒',
        placement: 'bottomRight',
        description: text,
        key: key,
        btn: function btn(h) {
          return h('a-button', {
            props: {
              type: 'primary',
              size: 'small'
            },
            on: {
              click: function click() {
                return _this5.showDetail(key, data);
              }
            }
          }, '查看详情');
        }
      });
    },
    showDetail: function showDetail(key, data) {
      var _this6 = this;

      this.$notification.close(key);
      var id = data.msgId;
      getAction(this.url.queryById, {
        id: id
      }).then(function (res) {
        if (res.success) {
          var record = res.result;

          _this6.showAnnouncement(record);
        }
      });
    }
  }
};
