import moment from 'moment'
import { getAction } from '@/api/manage'

moment.locale('zh-cn')

export default {
  data() {
    return {
      advanced: false,
      dataSource: [],
      pagination: {
        defaultPageSize: 10,
        defaultCurrent: 1,
        pageSizeOptions: ['10', '20', '30', '40', '100'],
        showQuickJumper: true,
        showSizeChanger: true,
        showTotal: (total, range) => `显示 ${range[0]} ~ ${range[1]} 条记录，共 ${total} 条记录`
      },
      loading: true,
      tableLoading: true
    }
  },
  computed: {
    columns() {
      return [{
        title: '请求时间',
        dataIndex: 'timestamp',
        customRender(text) {
          return moment(text).format('YYYY-MM-DD HH:mm:ss')
        }
      }, {
        title: '请求方法',
        dataIndex: 'request.method',
        scopedSlots: { customRender: 'requestMethod' },
        filters: [
          { text: 'GET', value: 'GET' },
          { text: 'POST', value: 'POST' },
          { text: 'PUT', value: 'PUT' },
          { text: 'DELETE', value: 'DELETE' }
        ],
        filterMultiple: true,
        onFilter: (value, record) => record.request.method.includes(value)
      }, {
        title: '请求URL',
        dataIndex: 'request.uri',
        customRender(text) {
          return text.split('?')[0]
        }
      }, {
        title: '响应状态',
        dataIndex: 'response.status',
        scopedSlots: { customRender: 'responseStatus' }
      }, {
        title: '请求耗时',
        dataIndex: 'timeTaken',
        scopedSlots: { customRender: 'timeTaken' }
      }]
    }
  },
  mounted() {
    this.fetch()
  },
  methods: {

    handleClickUpdate() {
      this.fetch()
    },

    handleTableChange() {
      this.fetch()
    },

    fetch() {
      this.tableLoading = true
      getAction('actuator/httptrace').then((data) => {
        let filterData = []
        for (let d of data.traces) {
          if (d.request.method !== 'OPTIONS' && d.request.uri.indexOf('httptrace') === -1) {
            filterData.push(d)
          }
        }
        this.dataSource = filterData
      }).catch((e) => {
        console.error(e)
        this.$message.error('获取HTTP信息失败')
      }).finally(() => {
        this.loading = false
        this.tableLoading = false
      })
    }

  }
}
