import JSelectDepartModal from './modal/JSelectDepartModal'
export default {
  name: 'JSelectDepart',
  components:{
    JSelectDepartModal
  },
  props:{
    modalWidth:{
      type:Number,
      default:500,
      required:false
    },
    multi:{
      type:Boolean,
      default:false,
      required:false
    },
    rootOpened:{
      type:Boolean,
      default:true,
      required:false
    },
    value:{
      type:String,
      required:false
    },
    disabled:{
      type: Boolean,
      required: false,
      default: false
    }
  },
  data(){
    return {
      visible:false,
      confirmLoading:false,
      departNames:"",
      departIds:''
    }
  },
  mounted(){
    this.departIds = this.value
  },
  watch:{
    value(val){
      this.departIds = val
    }
  },
  methods:{
    initComp(departNames){
      this.departNames = departNames
    },
    openModal(){
      this.$refs.innerDepartSelectModal.show()
    },
    handleOK(rows,idstr){
      console.log("当前选中部门",rows)
      console.log("当前选中部门ID",idstr)
      if(!rows){
        this.departNames = ''
        this.departIds=''
      }else{
        let temp = ''
        for(let item of rows){
          temp+=','+item.departName
        }
        this.departNames = temp.substring(1)
        this.departIds=idstr
      }
      this.$emit("change",this.departIds)
    },
    getDepartNames(){
      return this.departNames
    },
    handleEmpty(){
      this.handleOK('')
    }
  },
  model: {
    prop: 'value',
    event: 'change'
  }
}
