import JCronModal from "./modal/JCronModal";
export default {
  name: 'JCron',
  components: {
    JCronModal
  },
  props: {
    value: {
      required: false,
      type: String,
      default:()=>{
        return '* * * * * ? *'
      }
    }
  },
  data(){
    return {
      cron: this.value,
    }
  },
  watch:{
    value(val){
      this.cron = val
    }
  },
  methods:{
    openModal(){
      this.$refs.innerVueCron.show();
    },
    handleOK(val){
      this.cron = val;
      this.$emit("change", this.cron);
      //this.$emit("change", Object.assign({},  this.cron));
    },
    handleEmpty(){
      this.handleOK('')
    }
  },
  model: {
    prop: 'value',
    event: 'change'
  }
}
