import { queryDepartTreeList } from '@/api/api'
export default {
  name: 'JSelectDepartModal',
  props:['modalWidth','multi','rootOpened','departId'],
  data(){
    return {
      visible:false,
      confirmLoading:false,
      treeData:[],
      autoExpandParent:true,
      expandedKeys:[],
      dataList:[],
      checkedKeys:[],
      checkedRows:[],
      searchValue:""
    }
  },
  created(){
    this.loadDepart();
  },
  watch:{
    departId(){
      this.initDepartComponent()
    },
    visible: {
      handler() {
        if (this.departId) {
          this.checkedKeys = this.departId.split(",");
          console.log('this.departId', this.departId)
        } else {
          this.checkedKeys = [];
        }
      }
    }
  },
  methods:{
    show(){
      this.visible=true
      this.checkedRows=[]
      this.checkedKeys=[]
      console.log("this.multi",this.multi)
    },
    loadDepart(){
      queryDepartTreeList().then(res=>{
        if(res.success){
          let arr = [...res.result]
          this.reWriterWithSlot(arr)
          this.treeData = arr
          this.initDepartComponent()
          if(this.rootOpened){
            this.initExpandedKeys(res.result)
          }
        }
      })
    },
    initDepartComponent(){
      let names = ''
      if(this.departId){
        let currDepartId = this.departId
        for(let item of this.dataList){
          if(currDepartId.indexOf(item.key)>=0){
            names+=","+item.title
          }
        }
        if(names){
          names = names.substring(1)
        }
      }
      this.$emit("initComp",names)
    },
    reWriterWithSlot(arr){
      for(let item of arr){
        if(item.children && item.children.length>0){
          this.reWriterWithSlot(item.children)
          let temp = Object.assign({},item)
          temp.children = {}
          this.dataList.push(temp)
        }else{
          this.dataList.push(item)
          item.scopedSlots={ title: 'title' }
        }
      }
    },
    initExpandedKeys(arr){
      if(arr && arr.length>0){
        let keys = []
        for(let item of arr){
          if(item.children && item.children.length>0){
            keys.push(item.id)
          }
        }
        this.expandedKeys=[...keys]
      }else{
        this.expandedKeys=[]
      }
    },
    onCheck (checkedKeys,info) {
      if(!this.multi){
        let arr = checkedKeys.checked.filter(item=>{
          return this.checkedKeys.indexOf(item)<0
        })
        this.checkedKeys = [...arr]
        this.checkedRows=[info.node.dataRef]
      }else{
        this.checkedKeys = checkedKeys.checked
        this.checkedRows.push(info.node.dataRef)
      }
      //this.$emit("input",this.checkedKeys.join(","))
      //console.log(this.checkedKeys.join(","))
    },
    onSelect (selectedKeys,info) {
      console.log(selectedKeys)
      let keys = []
      keys.push(selectedKeys[0])
      if(!this.checkedKeys || this.checkedKeys.length==0 || !this.multi){
        this.checkedKeys = [...keys]
        this.checkedRows=[info.node.dataRef]
      }else{
        let currKey = info.node.dataRef.key
        if(this.checkedKeys.indexOf(currKey)>=0){
          this.checkedKeys = this.checkedKeys.filter(item=>{
            return item !=currKey
          })
          this.checkedRows=this.checkedRows.filter(item=>{
            return item.key !=currKey
          })
        }else{
          this.checkedRows.push(info.node.dataRef)
          this.checkedKeys.push(...keys)
        }
      }
    },
    onExpand (expandedKeys) {
      this.expandedKeys = expandedKeys
      this.autoExpandParent = false
    },
    handleSubmit(){
      if(!this.checkedKeys || this.checkedKeys.length==0){
        this.$emit("ok",'')
      }else{
        this.$emit("ok",this.checkedRows,this.checkedKeys.join(","))
      }
      this.handleClear()
    },
    handleCancel(){
      this.handleClear()
    },
    handleClear(){
      this.visible=false
      this.checkedKeys=[]
    },
    getParentKey(currKey,treeData){
      let parentKey
      for (let i = 0; i < treeData.length; i++) {
        const node = treeData[i]
        if (node.children) {
          if (node.children.some(item => item.key === currKey)) {
            parentKey = node.key
          } else if (this.getParentKey(currKey, node.children)) {
            parentKey = this.getParentKey(currKey, node.children)
          }
        }
      }
      return parentKey
    },
    onSearch(value){
      const expandedKeys = this.dataList.map((item) => {
        if (item.title.indexOf(value) > -1) {
          return this.getParentKey(item.key,this.treeData)
        }
        return null
      }).filter((item, i, self) => item && self.indexOf(item) === i)

      Object.assign(this, {
        expandedKeys,
        searchValue: value,
        autoExpandParent: true,
      })


    }
  }
}
