import JGraphicCode from '@/components/jeecg/JGraphicCode'
import { getAction } from  '@/api/manage'
import {checkOnlyUser } from '@/api/api'
export default {
  name: "Step1",
  components: {
    JGraphicCode
  },
  data () {
    return {
      form: this.$form.createForm(this),
      inputCodeContent: "",
      inputCodeNull:true,
      verifiedCode:"",
      validatorRules: {
        username:{rules: [{ required: false},{validator: this.validateInputUsername}]},
        inputCode:{rules: [{ required: true, message: '请输入验证码!'},{validator: this.validateInputCode}]},
      },

    }
  },
  methods: {
    nextStep () {
      let that = this
      this.form.validateFields((err, values) => {
        if (!err){
        var params={}
        var reg=/^[1-9]\d*$|^0$/;
        var username=values.username;
        if(reg.test(username)==true) {
          params.phone=username;
        }else{
          params.username=username;
        }
          getAction("/sys/user/querySysUser",params).then((res)=>{
            if(res.success){
          var userList={
            username:res.result.username,
            phone:res.result.phone
          };
           setTimeout(function () {
            that.$emit('nextStep',userList)
          })
        }
        });
      }
  })

    },
    validateInputCode(rule, value, callback){
      if (!value || this.verifiedCode == this.inputCodeContent) {
        callback();
      } else {
        callback(new Error("您输入的验证码不正确!"));
      }
    },
    inputCodeChange(e){
      this.inputCodeContent = e.target.value;
      console.log(this.inputCodeContent)
      if(!e.target.value||0==e.target.value){
        this.inputCodeNull=true
      }else{
        this.inputCodeContent = this.inputCodeContent.toLowerCase()
        this.inputCodeNull=false
      }
    },
    generateCode(value){
      this.verifiedCode = value.toLowerCase();
      console.log(this.verifiedCode);
    },
    validateInputUsername(rule, value, callback){
      console.log(value);
      var reg=/^[0-9]+.?[0-9]*/;
      if(!value){
        callback("请输入用户名和手机号！");
      }

      //判断用户输入账号还是手机号码
      if(reg.test(value)){
        var params = {
          phone : value,
        };
        checkOnlyUser(params).then((res) => {
          if (res.success) {
          callback("用户名不存在!")
        } else {
          callback()
        }
      })
      }else{
        var params = {
          username: value,
        };
        checkOnlyUser(params).then((res) => {
          if (res.success) {
            callback("用户名不存在!")
          } else {
            callback()
          }
        })
      }
    },

  }
}
